import Link from "next/link";
import Header from "../components/header";
import Layout from "../components/layout";
import React, { useContext, useEffect } from "react";
import PodcastContext from '../components/context/podcastContext';
import {UPDATE_CURRENT_PODCAST} from '..//components/context/reducers'

const Index = () => {
    const podcastContext = useContext(PodcastContext)

    useEffect(()=> {
        debugger;
        if(podcastContext.podcastState.currentPodcast == "") {
            podcastContext.updateCurrentPodcast()
            // podcastContext.dispatch({type: UPDATE_CURRENT_PODCAST, value: 'test'})
        }
    }, [])
    // console.log(useContext(PodcastContext));
  return (
    <Layout>
      <p>Hello Next.js</p>
    </Layout>
  );
};

export default Index;
