import gql from 'graphql-tag';

export const PodcastsQuery = gql `
query Podcasts {
    podcasts {
        description,
        title,
        image {
            url
            id
            mime
          },
        id
    }
}
`;


export const LatestPodcastQuery = (id) =>  gql `
query Podcasts {
    podcast(id)
}
`
