import React, { useReducer } from "react";
import {
  podcastReducer,
  UPDATE_CURRENT_PODCAST,
  UPDATE_PODCASTS_LIST
} from "./reducers";
import PodcastContext from "./podcastContext";

const GlobalState = props => {
//   const [theme, setTheme] = useState("light");
//   const [podcastList, setPodcastList] = useState({});
//   const [currentPodcast, setCurrentPodcast] = useState({});
  const theme = "light";
  const podcastList = [];
  const currentPodcast= {};
  // state = {
  //     theme: 'light',
  //     podcastList: [],
  //     currentPodcast: {}
  // }

  const [podcastState, dispatch] = useReducer(podcastReducer);

  const updateCurrentPodcast = (podcast) => {
      dispatch({type: UPDATE_CURRENT_PODCAST, podcast})
  };

  const updatePodcastList = (podcasts) => {
      dispatch({type: UPDATE_PODCASTS_LIST, podcasts})
  }

  return <PodcastContext.Provider value={{updateCurrentPodcast, updatePodcastList, podcastState}}>{props.children}</PodcastContext.Provider>;
};


export default GlobalState;
