import React, { FunctionComponent } from 'react';
import * as Interfaces from '../services/interface';
import {Card, CardHeader, CardImage, CardContent, Content, Title, Image} from 'bloomer'
// style={{width: "100%", height: "10vh"}}
const PodcastCard:FunctionComponent = (props: Interfaces.Podcast) => {
return (
    <Card style={{width: "50vw", height: "30vh"}}>
        <CardHeader>
            <Image className={"image is-3by1"} src={props.image.url} alt={props.title} />
        </CardHeader>
        <CardContent>
            <Title>{props.title}</Title>
            <Content>{props.description}</Content>
        </CardContent>
    </Card>
)

}


export default PodcastCard
