import gql from "graphql-tag";

export const PodcastsQuery = gql`
  query Podcasts {
    podcasts {
      description
      title
      image {
        url
        id
        mime
      }
      id
    }
  }
`;

export const LatestPodcastQuery  = gql`
    query Podcasts($someId: ID!) {
      podcast(id: $someId) {
        description
        title
        image {
          url
          id
          mime
        }
        id
        audioFile {
          url
          id
          mime
        }
      }
    }
  `;


  export const SelectedPodcastQuery  = gql`
    query Podcasts($someId: ID!) {
      podcast(id: $someId) {
        description
        title
        image {
          url
          id
          mime
        }
        id
        audioFile {
          url
          id
          mime
        }
      }
    }
  `;


  export const SinglePodcast  = gql`
    query Podcasts($filter: String!) {
      podcasts(limit: 1, where: {title: $filter}) {
        description
        title
        image {
          url
          id
          mime
        }
        id
        audioFile {
          url
          id
          mime
        }
      }
    }
  `;



  export const Latest  = gql`
    query Podcasts($filter: String!) {
      podcasts(limit: 1, sort: {dateCreated: $filter}) {
        description
        title
        image {
          url
          id
          mime
          create_by
        }
        id
        audioFile {
          url
          id
          mime
        }
      }
    }
  `;
