import Link from "next/link";
import Header from "../components/header";
import Layout from "../components/layout";
import React, { useContext, useEffect } from "react";
import PodcastContext from '../components/context/podcastContext';
import {UPDATE_CURRENT_PODCAST} from '..//components/context/reducers'

const Index = () => {
    const podcastContext = useContext(PodcastContext)

    useEffect(()=> {
        debugger;
        if(podcastContext.podcastState.currentPodcast == "") {
            podcastContext.dispatch(UPDATE_CURRENT_PODCAST, 'test')
        }
    }, podcastContext.currentPodcast)
    // console.log(useContext(PodcastContext));
  return (
    <Layout>
      <p>Hello Next.js</p>
    </Layout>
  );
};

export default Index;
