import { useRouter } from 'next/router';
import Layout from '../components/layout';
import React, { useEffect, useState, useContext } from 'react';
import { useQuery } from '@apollo/react-hooks';
import { SelectedPodcastQuery,SinglePodcast } from '../services/query';
import _ from 'lodash'
import { Box, Tile, Title , Content} from 'bloomer';
import {Podcast} from '../services/interface'
import PodcastContext from '../components/context/podcastContext';
import AudioPlayer from '../components/audioPlayer';

const PodcastPage = () => {
    const router = useRouter();
    const {podcastState, updatePodcastList, updateCurrentPodcast} = useContext(PodcastContext);
    // console.log(PodcastSelectedContext)

    // const { data, loading, error } = useQuery(SelectedPodcastQuery, { variables: { someId: podcastState.currentPodcast.id } })
    const { data, loading, error } = useQuery(SinglePodcast, { variables: { filter: router.query.title } })

    const [podcast, setPodcast] = useState<Podcast | null>(null);


    useEffect(() => {
        if (data && !_.isEqual(data.podcast, podcast)) {
            setPodcast(data.podcast)
        }
    }, [data]);


    useEffect(()=> {
        if(error) {
            window.location.href =  window.location.origin;
        }
    }, [error])

    const handleLayout = () => {
        return (
            <section className="section">
            <div className="container">
                <Tile isAncestor>
                    <Tile isParent isVertical className="is-4">
                        <img style={{height: '100%', objectFit: "cover", width: '100%'}} src={cardUrlHandler(podcast.image.url)} alt={podcast.title}/>
                    </Tile>
                    <Tile isParent>
                        <Box>
                            <Title>{podcast.title}</Title>
                            {/* <audio controls>
                                <source src={cardUrlHandler(podcast.audioFile.url)} type={podcast.audioFile.mime} id={podcast.audioFile.id}/>
                            </audio> */}
                            <AudioPlayer {...podcast.audioFile} />
                            <Content>{podcast.description}</Content>
                        </Box>
                    </Tile>
                </Tile>
            </div>
        </section>
        )
    }

    return (
        <Layout>
           {podcast ? handleLayout(): null}
        </Layout>
    )
}


export default PodcastPage



const cardUrlHandler = (url) => {
    return `http://localhost:1337/${url}`
   }
