import React from 'react';

function Bar(props) {
    const {duration, currentTime, onTimeUpdate} = props;


    const calculateTime = (e) => {
        const clickedPosition = e.pageX;
        const bar = document.getElementById("progressBar");
        const barStart = bar.getBoundingClientRect()
    }

    const handleTimeDrag = (e) => {
        onTimeUpdate(calculateTime(e));
    }

    return (
        <div className="bar">
            <span>{currentTime}</span>
            <progress className="progress is-primary" value={currentTime} max={duration} onMouseDown={e => handleTimeDrag(e)}>

            {/* <span>

            </span> */}
            </progress>
            <span>{duration}</span>
        </div>
    )
}


export default Bar;
