import React from 'react';

function Bar(props) {
    const { duration, currentTime, onTimeUpdate } = props;


    const calculateTime = (e) => {
        const clickedPositionOnPage = e.pageX;
        const bar = document.getElementById("progress");
        const barStart = bar.getBoundingClientRect().left + window.scrollX;
        const barWidth = bar.offsetWidth;
        const clickPositionInBar = clickedPositionOnPage - barStart;
        const timePerPixel = duration / barWidth;
        debugger;
        return timePerPixel * clickPositionInBar;
    }

    const handleTimeDrag = (e) => {
        onTimeUpdate(calculateTime(e));
    }

    return (
        <div className="bar">
            <span>{currentTime}</span>
            <progress id="progress" className="progressBar" value={currentTime} max={duration} onMouseDown={e => handleTimeDrag(e)}>

                {/* <span>

            </span> */}
            </progress>
            <span>{duration}</span>
        </div>
    )
}


export default Bar;
