import React, { FunctionComponent, useRef, useState } from 'react';
import { AudioFile } from '../services/interface'
import { Howl, Howler } from 'howler';
import { Box } from 'bloomer';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlayCircle, faPauseCircle } from '@fortawesome/free-solid-svg-icons'

const AudioPlayer: FunctionComponent<AudioFile> = ({ url, id, mime }) => {

    const [volume, setVolume] = useState(0.5);
    const [playRate, setPlayRate] = useState(1);
    const [play, setPlay] = useState(false);

    const audio = new Howl({
        src: [cardUrlHandler(url)],
        volume: volume,
        html5: true,
        rate: playRate,
        onend: () => {
            console.log("the end")
        },
        onpause:()=> {

        }
    })

    return (
        // <div >
        //     <audio controls>
        //         <source src={url} type={mime} id={id} />
        //     </audio>
        // </div>
        <Box>
            <FontAwesomeIcon icon={!play ? faPlayCircle: faPauseCircle} onClick={() => {

                audio.playing() ? audio.pause() : audio.play();
                setPlay(!play);
                console.log(audio.duration())
            }} />
        </Box>
    )
}


export default AudioPlayer;



const cardUrlHandler = (url) => {
    return `http://localhost:1337/${url}`
}
